// jQuery에서 input에 쓴 값을 읽고 append로 <li> 추가했던 것처럼,
// React에서는 input값도 useState로 관리하고, 버튼 클릭 시 todos 배열을 업데이트하는 함수로 처리한다. 

export default function Todo() {
    const [todos, setTodos] = useState([
        { id: 1, text: '청소하기' },

    ]);

    // input 값 상태
    const [inputValue, setInputValue] = useState('');

    // 추가 함수
    const addTodo = () => {
        const trimmed = inputValue.trim();
        if (trimmed === '') {
            alert('값을 추가해주세요.');
            return;
        }

        // 이전 배열에 새 할 일 추가
        setTodos([...todos, { id: Date.now(), text: trimmed }]);
        setInputValue(''); // 입력창 초기화
    };

    return (
        <div>
            <h2>할 일 목록</h2>
            <ul>
                {todos.map(todo => (
                    <li key={todo.id}>{todo.text}</li>
                ))}
            </ul>

            <input
                type="text"
                placeholder="할 일을 입력하세요"
                value={inputValue}
                onChange={e => setInputValue(e.target.value)} // 입력 변화 감지
            />
            <button onClick={addTodo}>할일목록추가</button>
        </div>
    );
}
